import { useNavigate, useParams } from "react-router-dom";
import styled from "styled-components";
import Button from "../components/Button/Button";
import FormItem from "../components/FormItem/FormItem";
import NavBar from "../components/NavBar/NavBar";




const FormWrapper = styled.div `
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  margin: 2% 5%;
`;

const SubmitButton = styled(Button)
`
  background: blue;
  margin: 2% 0;
`;


const ListForm = () => {
    let navigate = useNavigate()
    const { listId } = useParams()

    const [title, setTitle] = useState('')
    const [quantity, setQuantity] = useState('')
    const [price, setPrice] = useState('')

    return (
        <React.Fragment>
    { navigate && <NavBar goBack={() => navigate(-1)} title={`Ajouter un Element`} />}
    <FormWrapper>
      <form>
        <FormItem id='title' label='Title' placeholder='Nouvel element' />
        <FormItem 
          id = 'quantity'
          label='quantity'
          type='number'
          placeholder='0'
        />
        <FormItem id='price' label='Price' type='number' palceholder='0.00' />
        <SubmitButton>{`Ajouter l'element`}</SubmitButton>
      </form>

    </FormWrapper>


    </React.Fragment>
    )

}

export default ListForm
