import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import styled from "styled-components";
import NavBar from "../components/NavBar/NavBar";
import useDataFetching from "../hooks/useDataFetching";




// params(no liste), useDataFetching, composant ListItem...


const ListItemWrapper = styled.div `
display: flex;
justify-content: space-between;
flex-direction: column;
margin: 2% 5%;
`;

const ListDetail = () => {
    const { listId } = useParams()

    const [loading, error, data] = useDataFetching('http://localhost:8000/items');

    const [items, setItems] = useState([])

    useEffect(() => {
        data && listId && setItems(data.filter(item => item.listId === parseInt(listId)))
    }, [data, listId])


    return (
        <ListItemWrapper>
          { loading || error ? (
            <span>{error || 'En cours de chargement...'}</span>
            ) : (
            items.map(item => <ListItem key={item.id} {...item} />)
            )}
        </ListItemWrapper>
    )
}

export default ListDetail
