  display: flex;
  text-align: left;
  align-items: center;
  padding: 1%;
  background: lightGray;
  border-radius: 5px;
  padding: 10px;
  margin-bottom: 2%;
  text-decoration: none;
`;

const Title = styled.h3`
  flex-basis: 70%;
`;

const Total = styled.span`
  flex-basis: 15%;
  font-weight: bold;
  text-align: right;
`;

const ListItem = () => {
  return (
    <ListItemWrapper>
        <Title></Title>
        <Total></Total> {/* quantity */}
        <Total></Total> {/*  price */}
    </ListItemWrapper>
  )
}

