import React, { useEffect, useContext } from "react";
import { useNavigate, useParams } from "react-router-dom";
import styled from "styled-components";
import NavBar from "../components/NavBar/NavBar";
import ListItem from "../components/ListItem/ListItem"
import { ItemsContext } from "../contexts/ItemsContext";




// params(no liste), useDataFetching, composant ListItem...


const ListItemWrapper = styled.div `
display: flex;
justify-content: space-between;
flex-direction: column;
margin: 2% 5%;
`;

const ListDetail = () => {
    const { listId } = useParams()
    let navigate = useNavigate()

    const { items, error, loading, fetchItems } = useContext(ItemsContext)

    useEffect(() => {
        items.length && listId && fetchItems(listId)
    }, [items, listId])


    return (

        <React.Fragment>
         { navigate && <NavBar  openForm={() => navigate(`/list/${listId}/new`)} /> }
        <ListItemWrapper>
          { loading || error ? (
            <span>{error || 'En cours de chargement...'}</span>
            ) : (
            items.map(item => <ListItem key={item.id} {...item} />)
            )}
        </ListItemWrapper>

        </React.Fragment>
    )

}

export default ListDetail
