import { createContext, useReducer, useCallback } from 'react'
import useDataFetching from '../hooks/useDataFetching'


export const ListsContext = createContext()

// 1 - definir un etat initial
const initialState = {
    lists: [],
    loading: true,
    error: ''
}

// 2 - definir des actions
// charger les listes ==> CHARGEMENT_LISTES ==> remplir tableau listes

const reducer = (state, action) => {
    switch (action.type) {
        case 'GET_LISTS_SUCCESS':
            return {
                ...state,
                lists: action.payload,
                loading: false
            };
        case 'GET_LISTS_ERROR':
            return {
                ...state,
                lists: [],
                loading: false,
                error: action.payload
            };
        default:
            return state
    }
}

export const ListsContextProvider = ({ children }) => {
    const [loading, error, data] = useDataFetching('http://localhost:8000/lists');


    return (

        <ListsContext.Provider value={{lists: data, loading, error}}>{children}</ListsContext.Provider>
    )
}

export default ListsContextProvider
