import styled from 'styled-components';

const ButtonDelete = styled.button`
  display: flex;
  justify-content: center;
  align-items: center;
  background: #ff0038;
  color: white;
  padding: 7px;
  line-height: 2;
  border-radius: 5px;
  border: 0;
  font-size: inherit;
  cursor: pointer;
  margin: 3px;
  height: 50%;
`;

export default ButtonDelete;
