import { createContext, useCallback, useReducer } from 'react'
import useDataFetching from '../hooks/useDataFetching'
import { v4 as uuidv4 } from 'uuid';

export const ItemsContext = createContext()

const initialState = {
    items: [],
    loading: false,
    error: ''
}

const reducer = (state, action) => {
    switch (action.type) {
        case 'GET_ITEMS_SUCCESS':
            return {
                ...state,
                items: action.payload,
                loading: true
            };
        case 'GET_ITEMS_ERROR':
            return {
                ...state,
                items: [],
                loading: false,
                error: action.payload
            };
        case 'ADD_ITEM_SUCCESS':
            return {
                ...state,
                items: [...state.items, action.payload],
                loading: true
            };
        case 'DELETE_ITEM_SUCCESS':
            const newList = state.items.filter(item => item.id !== action.payload)
            return {
                ...state,
                items : [...newList],
                error: ''
            }
        case 'UPDATE_ITEM_SUCCESS' : 
            const editedItem = action.payload
            const listItems = [...state.items]
            const idEditedItem = editedItem.id
            const listWithoutEditedItem = listItems.filter( function(item) {
                return idEditedItem.indexOf(item.id) === -1
            })
            return {
                ...state,
                items : [editedItem, ...listWithoutEditedItem],
                error: ''
            }
        default:
            return state
    }
}

export const ItemsContextProvider = ({ children }) => {
    // const [loading, error, data] = useDataFetching('http://localhost:8000/items');

    const [state, dispatch] = useReducer(reducer, initialState)

    const fetchItems = useCallback(
        async (listId) => {
            try {
                const data = await fetch(`http://localhost:8000/lists/${listId}/items`);
                const result = await data.json();

                if (result) {
                    dispatch({ type: 'GET_ITEMS_SUCCESS', payload: result })
                }
            } catch (error) {
                dispatch({ type: 'GET_ITEMS_ERROR', payload: error.message })
            }
        }, []
    )

    const addItem = useCallback(async (item) => {
        try {
            const data = await fetch(`http://localhost:8000/items`, {
                method: 'POST',
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(item)
            })
            if (data) {
                dispatch({
                    type: 'ADD_ITEM_SUCCESS',
                    payload: item
                })
            }
        } catch {}
    }, [])    

    const deleteItem = useCallback(async (id) => {
        try {

            dispatch({type: 'DELETE_ITEM_SUCCESS', payload: id})
            await fetch(`http://localhost:8000/items/${id}`, {
                method: 'DELETE',
            })
            } catch {}
    }, [])

    const updateItem = useCallback(async (item) => {
        try {
            const data = await fetch(`http://localhost:8000/items/${id}`, {
                method: 'PUT',
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(item)
            })
            if (data) {
                dispatch({
                    type: 'UPDATE_ITEM_SUCCESS',
                    payload: item
                })
            }
        } catch (error) {

        }
    })

    return (

        <ItemsContext.Provider value={{...state, fetchItems, addItem, deleteItem}}>{children}</ItemsContext.Provider>
    )
}

export default ItemsContextProvider
